package tareaIterando;

public class bucleWhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		   int suma = 0;
	        int contador = 1;
	        
	        while (contador <= 10) {
	            suma += contador; 
	            contador++; 
	        }
	        

	        System.out.println("La suma de los nÃºmeros del 1 al 10 es: " + suma);
	}

}
